mapper.select:
SELECT COUNT(*) as cnt
FROM admin_stock
WHERE admin_id=#{admin_id} AND passwd=#{passwd}

SELECT admin_num, admin_id, passwd, mname, mdate, grade 
FROM admin_stock
WHERE admin_id=#{admin_id}

SELECT admin_num, admin_id, passwd, mname, mdate, grade 
FROM admin_stock
WHERE admin_num=#{admin_num}
