mapper.select:
SELECT COUNT(*) as cnt FROM contents_stock
WHERE cate_id=#{cate_id}
WHERE cate_id=#{cate_id} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')

SELECT contents_num, admin_num, cate_id, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, r FROM ( SELECT contents_num, admin_num, cate_id, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, rownum as r FROM ( SELECT contents_num, admin_num, cate_id, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube FROM contents_stock
WHERE cate_id=#{cate_id}
WHERE cate_id=#{cate_id} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY contents_num DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM contents_stock
WHERE contents_num = #{contents_num} AND passwd=#{passwd}

mapper.update:
UPDATE contents_stock
SET title=#{title}, content=#{content},  word=#{word}
WHERE contents_num = #{contents_num}

UPDATE contents_stock
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE contents_num = #{contents_num}

